sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '' || newValue == oldValue) {
      return;
   }

	// create an instance of the GlideAjax's extension "GetEmailAddress"
	var getEmailAddr = new GlideAjax('GetEmailAddress');
	// specify the getEmail method (from my extended version of GlideAjax)
	getEmailAddr.addParam('sysparm_name', 'getEmail');
	// pass the id of the "Request for"
	getEmailAddr.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
	// send request
	getEmailAddr.getXML(populateEmailField);

	function populateEmailField(response) {
		// pull just the email from the server's response, 
		var email = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue(('u_requested_for_email'));
		g_form.setValue('u_requested_for_email', email);
		// clear the email field so we can replace it with what the server found
	}
}
